
public class printingNumberInPyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;
		
		for(int i=0;i<=3;i++)
		{
			for(int j=3;j>=i;j--)
				
				
			{
				
				System.out.print(num+" ");
				num--;
				
			}
			System.out.println();
		}

	}

}
